import { useContext, useEffect, useState } from "react";
import { Burger } from "../UI/Burger/Burger";
import "./Heade.css";
import { AppContext } from "../../context/AppContext";
import { FilterIcon } from "../UI/FilterIcon/FilterIcon";

const Header = () => {
  const { point, setPoint, searchQuery, setSearchQuery, showSearch, setShowSearch } =
    useContext(AppContext);
  
  const [showInput, setShowInput] = useState(false);
  const [showNav, setShowNav] = useState(false);
  const [shadow, setShadow] = useState(false);

  const burgerMenu = () => {
    if (showNav) {
      setShowNav(false);
      document.querySelector(".ambient").style.animation = "hide .4s";
      setTimeout(() => setShadow(false), 350);
    } else {
      setShowNav(true);
      document.querySelector(".ambient").style.animation = "show .4s";
      setShadow(true);
    }
  };

  useEffect(() => {
    if (showInput) document.querySelector('imput').focus();
  }, [showInput])

  return (
    <div className="header">
      <div className="header__items">
        {showSearch && <FilterIcon onClick={() => setShowInput(!showInput)}/>}
        {showInput && <input value={searchQuery} onChange={e => setSearchQuery(e.target.value)} />}
        <Burger onClick={burgerMenu} />
      </div>
      <div className={shadow ? "ambient active" : "ambient"} />
      <div className={showNav ? "header__nav active" : "header__nav"}></div>
    </div>
  );
};

export default Header;
