import { useState } from "react";
import { Burger } from "../UI/Burger/Burger";
import "./Heade.css";

const Header = () => {
  const [showNav, setShowNav] = useState(false);

  if (!showNav) document.querySelector('.ambient').style.animation = "hide .4s"

  return (
    <div className="header">
      <div className="header__items">
        <Burger onClick={() => setShowNav(!showNav)}/>
      </div>
        <div className={showNav ? "ambient active" : "ambient close"} />
      <div className={showNav ? "header__nav active" : "header__nav" }>
      </div>
    </div>
  );
};

export default Header;
