import { Link } from "react-router-dom";
import ProductItem from "./ProductItem";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { addFavouriteAction } from "../../store/favouriteReducer";

const ProductList = ({ products, reff, ...props }) => {

  const dispatch = useDispatch();
  const favourites = useSelector( state => state.favourites );

  useEffect(() => {
    products.forEach(product => {
      if (localStorage.getItem(product.title)) {
        dispatch(addFavouriteAction(product));
      }
    });
  }, []);

  if (!products.length) {
    return (
      <div className="notFound">
        <p>Такого товару не знайдуно</p>
        <Link className="back" to="/menu">Повернутись</Link>
      </div>
    );
  }

  return (
    <div {...props} ref={reff} className="productList">
      {products.map( item => 
      <ProductItem key={item.id} product={item}/>
    )}
    </div>
  );
};

export default ProductList;
