import Flickity from "react-flickity-component";
import "./flickity.css";
import cl from "./Carousel.module.scss";
import plate1 from "../../../assets/images/Carousel/1.webp";
import plate2 from "../../../assets/images/Carousel/2.webp";
import plate3 from "../../../assets/images/Carousel/3.webp";
import plate4 from "../../../assets/images/Carousel/4.webp";
import plate5 from "../../../assets/images/Carousel/5.webp";

const plates = [plate1, plate2, plate3, plate4, plate5];

const flickityOptions = {
  initialIndex: 2,
  wrapAround: true,
  autoplay: true,
  prevNextButtons: false,
};

const Carousel = () => {
  return (
    <Flickity
      className="slider"
      elementType="div"
      disableImagesLoaded={false}
      options={flickityOptions}
      reloadOnUpdate
      static
    >
      {plates.map((img) => (
        <div className={cl.div}>
          <div
            className={cl.plate}
            style={{ backgroundImage: `url(${img})` }}
          />
        </div>
      ))}
    </Flickity>
  );
};

export default Carousel;
