import { useContext, useEffect, useRef } from "react";
import { AppContext } from "../../context/AppContext";
import "./MenuPage.scss";
import { pizza, cocktail, juice, wine, strongAlco, bar_menu } from "../MenuComponents/products";
import MenuList from "../MenuComponents/MenuList";

const Menu_Page = () => {
  const { point, setPoint } = useContext(AppContext);
  
  const pizzaRef = useRef();
  const juiceRef = useRef();
  const cocktailRef = useRef();
  const strongAlcoRef = useRef();
  const wineRef = useRef();

  const barRef = useRef();

  const focusOn = ref => {
    ref.current.focus();
  };

  const scrollTo = ref => {
    window.scrollTo({
      top: ref.current.offsetTop,
      behavior: "smooth"
    });
  };

  

  console.log(point);

  return <div className="menu">
    <div className="menuPoints">
      <div className="menuPoint">
        <p>Піцца</p>
      </div>
      <div className="menuPoint">
        <p>Соки</p>
      </div>
      <div className="menuPoint">
        <p>Коктейлі</p>
      </div>
      <div className="menuPoint">
        <p>Алкоголь</p>
      </div>
      <div className="menuPoint">
        <p>Вина</p>
      </div>
      <div className="menuPoint">
        <p>Бар</p>
      </div>
    </div>
    { point === "pizza" &&  <MenuList tabIndex={15} reff={pizzaRef} products={pizza} /> }
    { point === "juice" &&  <MenuList tabIndex={16} reff={juiceRef} products={juice} />}
    { point === "cocktail" &&  <MenuList tabIndex={17} reff={cocktailRef} products={cocktail} />}
    { point === "alco" && <MenuList tabIndex={18} reff={strongAlcoRef} products={strongAlco} /> }
    { point === "wine" && <MenuList tabIndex={18} reff={wineRef} products={wine} /> }
  </div>;
};

export default Menu_Page;
