import { useEffect, useState } from "react";
import { Burger } from "../UI/Burger/Burger";
import "./Heade.css";

const Header = () => {
  const [showNav, setShowNav] = useState(false);
  const [shadow, setShadow] = useState(false);

  useEffect(() => {
    if (shadow) {
      document.querySelector('.ambient').animation = "show .4s";
    } else {
      document.querySelector('.ambient').animation = "hide 4s";
    }
  }, [shadow]);

  return (
    <div className="header">
      <div className="header__items">
        <Burger onClick={() => {
          if (showNav) {
            setShowNav(false);
            setShadow(false);
          } else {
            setShowNav(true);
            setShadow(true);
          }
        }}/>
      </div>
        <div className={showNav ? "ambient active" : "ambient"} />
      <div className={showNav ? "header__nav active" : "header__nav" }>
      </div>
    </div>
  );
};

export default Header;
