import { useState } from "react";
import { Burger } from "../UI/Burger/Burger";
import "./Heade.css";

const Header = () => {
  const [showNav, setShowNav] = useState(false);

  return (
    <div className="header">
      <div className="header__items">
        <Burger
          onClick={() => {
            if (showNav) {
              setTimeout(() => {
                setShowNav(false);
              }, 350);
            } else {
              setShowNav(true);
            }
          }}
        />
      </div>
      <div className={showNav ? "ambient active" : "ambient"} />
      <div className={showNav ? "header__nav active" : "header__nav"}></div>
    </div>
  );
};

export default Header;
