import { useContext, useState } from "react";
import ProductModal from "../UI/Modals/ProductModal/ProductModal";
import { AppContext } from "../../context/AppContext";

const ProductItem = ({ product }) => {
  const [openModal, setOpenModal] = useState(false);

  const [addFavourite, setAddFavourite] = useState(false);

  return (
    <>
      <div onClick={() => setOpenModal(true)} className="productItem">
        <div className="about">
          <p className="title">{product.title}</p>
          <p className="quantity">{product.quantity}</p>
          <p className="price">
            {product.price}
            <span>грн</span>
          </p>
          <div onClick={e => {
            e.stopPropagation();
            setAddFavourite(!addFavourite);
          }} className="">
            {addFavourite ? <p>&#9829; Улюблене</p> : <p>&#9825;</p>}
          </div>
        </div>
        <img className="img" src={product.image} alt="not found" />
      </div>
      <ProductModal
        product={product}
        visible={openModal}
        setVisible={setOpenModal}
      />
    </>
  );
};

export default ProductItem;
