import Flickity from "react-flickity-component";
import "./flickity.css";
import cl from "./Carousel.module.scss";
import p1 from "../../../assets/images/interior/1.jpg";
import p2 from "../../../assets/images/interior/2.webp";
import p3 from "../../../assets/images/interior/3.jpg";
import ProductModal from "../Modals/ProductModal/ProductModal";
import { useContext, useState } from "react";
import { AppContext } from "../../../context/AppContext";

const plates = [
  { img: p1, text: "Вітаємо Marry" },
  { img: p2, text: "Меню" },
  { img: p3, text: "Меню" },
];

const flickityOptions = {
  initialIndex: 2,
  wrapAround: true,
  autoPlay: true,
  prevNextButtons: true,
};

const Carousel = () => {
  const { isModalOpen, setIsModalOpen } = useContext(AppContext);

  return (
    <div className={cl.slider}>
      <Flickity
        className="slider"
        elementType="div"
        disableImagesLoaded={false}
        options={flickityOptions}
        reloadOnUpdate
        static
      >
        {plates.map((plate) => (
          <div className={cl.plate} key={plate.img}>
            <>
              <div
                onClick={() => setIsModalOpen(true)}
                className={cl.plateImg}
                style={{
                  backgroundImage: `url(${plate.img})`,
                }}
              >
                <p>{plate.text}</p>
              </div>
            </>
          </div>
        ))}
      </Flickity>
      <ProductModal visible={isModalOpen} setVisible={setIsModalOpen}/>
    </div>
  );
};

export default Carousel;
