import { useContext } from "react";
import ProductModal from "../UI/Modals/ProductModal/ProductModal";
import { AppContext } from "../../context/AppContext";
import { pizza } from "./products";

const ProductItem = ({ product }) => {
  const { isModalOpen, setIsModalOpen } = useContext(AppContext);

  return (
    <>
      <div onClick={() => setIsModalOpen(true)} className="productItem">
        <div className="about">
          <p className="title">{product.title}</p>
          <p className="quantity">{product.quantity}</p>
          <p className="price">
            {product.price}
            <span>грн</span>
          </p>
        </div>
        <img className="img" src={product.image} alt="not found" />
      </div>
      <ProductModal visible={isModalOpen} setVisible={setIsModalOpen} product={pizza[1]}/>
    </>
  );
};

export default ProductItem;
