import Flickity from "react-flickity-component";
import "./flickity.css";
import cl from "./Carousel.module.css";
import p1 from "../../../assets/images/interior/1.jpg";
import p2 from "../../../assets/images/interior/2.webp";
import p3 from "../../../assets/images/interior/3.jpg";

const plates = [
  {img: p1, text: "Меню",path: "/menu"},
  {img: p2, text: "Меню",path: "/menu"},
  {img: p3, text: "Меню",path: "/menu"},
];

const flickityOptions = {
  initialIndex: 2,
  wrapAround: true,
  autoPlay: true,
  prevNextButtons: false
};

const Carousel = () => {
  return (
    <div className={cl.slider}>
      <Flickity
        className="slider"
        elementType="div"
        disableImagesLoaded={false}
        options={flickityOptions}
        reloadOnUpdate
        static
      >
        { imgs.map(img =>
        <div className={cl.plate} key={img}>
          <>
            <div style={{
                backgroundImage: `url(${img})`,
                width: "100%",
                height: 250,
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover"
            }}/>
          </>
        </div>
        ) }
      </Flickity>
    </div>
  );
};

export default Carousel;
